// Función helper para obtener el icono del sprite
export const getSpriteTechIcon = (tech) => {
  if (!tech) return <SpriteTechIcon tech="database" />;
  return <SpriteTechIcon tech={tech} />;
};

// Exportación por defecto también para compatibilidad
export default getSpriteTechIcon;


